const hamburger=document.querySelector(".hamburger"),navLinks=document.querySelector(".menu__list"),links=document.querySelectorAll(".menu__list-item"),checkbox=document.querySelector("input[name=theme]");hamburger.addEventListener("click",()=>{navLinks.classList.toggle("open"),links.forEach(e=>{e.classList.toggle("fade")})}),checkbox.addEventListener("change",(function(){this.checked?document.documentElement.setAttribute("data-theme","dark"):document.documentElement.setAttribute("data-theme","light")}));